option.value:
.PHONY: help install start stop restart logs docker-up docker-down docker-build clean

help:  ## Show this help message
	@echo "Production Ready RAG - Makefile Commands"
	@echo "========================================"
	@grep -E '^[a-zA-Z_-]+:.*?## .*$$' $(MAKEFILE_LIST) | sort | awk 'BEGIN {FS = ":.*?## "}; {printf "\033[36m%-20s\033[0m %s\n", $$1, $$2}'

install:  ## Install dependencies
	python3 -m venv .venv
	. .venv/bin/activate && pip install -r requirements.txt

start:  ## Start all services using start.sh
	./start.sh

stop:  ## Stop all services using stop.sh
	./stop.sh

restart: stop start  ## Restart all services

logs:  ## Tail all logs
	tail -f fastapi.log inngest.log

docker-build:  ## Build Docker images
	docker-compose build

docker-up:  ## Start services with Docker Compose
	docker-compose up -d

docker-up-ui:  ## Start services with Docker Compose (including Streamlit UI)
	docker-compose --profile with-ui up -d

docker-down:  ## Stop Docker Compose services
	docker-compose down

docker-logs:  ## View Docker logs
	docker-compose logs -f

streamlit:  ## Start Streamlit UI
	streamlit run streamlit_app.py

clean:  ## Clean up temporary files and containers
	./stop.sh
	docker-compose down -v
	rm -f *.log
	rm -f .*.pid
	find . -type d -name __pycache__ -exec rm -rf {} +
	find . -type f -name "*.pyc" -delete

setup:  ## Complete setup (first time only)
	cp .env.example .env
	@echo "✅ Created .env file - please edit it and add your GEMINI_API_KEY"
	chmod +x start.sh stop.sh
	@echo "✅ Made scripts executable"
	@echo " Next steps:"
	@echo "   1. Edit .env and add your API keys"
	@echo "   2. Run: make start"

status:  ## Check status of all services
	@echo "Checking services..."
	@echo -n "FastAPI (8000): "
	@curl -s http://localhost:8000 > /dev/null && echo "✅ Running" || echo "❌ Not running"
	@echo -n "Qdrant (6333): "
	@curl -s http://localhost:6333 > /dev/null && echo "✅ Running" || echo "❌ Not running"
	@echo -n "Inngest (8288): "
	@curl -s http://localhost:8288 > /dev/null && echo "✅ Running" || echo "❌ Not running"

test:  ## Run basic tests
	@echo "Testing embedding function..."
	python debug_embed_search.py
